mapper.select:
SELECT A.*, M.nickname AS extra__writer, IFNULL(SUM(RP.point),0) AS extra__sumReactionPoint, IFNULL(SUM(IF(RP.point > 0,RP.point,0)),0) AS extra__goodReactionPoint, IFNULL(SUM(IF(RP.point < 0,RP.point,0)),0) AS extra__badReactionPoint FROM article AS A INNER JOIN `member` AS M ON A.memberId = M.id LEFT JOIN reactionPoint AS RP ON A.id = RP.relId AND RP.relTypeCode = 'article' WHERE 1
AND A.BoardId = #{boardId}
AND A.title LIKE CONCAT('%',#{searchKeyword},'%')
AND A.body LIKE CONCAT('%',#{searchKeyword},'%')
AND A.title LIKE CONCAT('%',#{searchKeyword},'%') OR A.body LIKE CONCAT('%',#{searchKeyword},'%')
GROUP BY A.id ORDER BY A.id DESC LIMIT #{limitFrom}, #{itemsInAPage};

mapper.update:
UPDATE article
title = #{title}, `body` = #{body}, updateDate= NOW(),
WHERE id = #{id}

UPDATE article
SET hitCount = hitCount + 1
WHERE id = #{id}

mapper.select:
SELECT hitCount
FROM article
WHERE id = #{id}

mapper.update:
UPDATE article
SET goodReactionPoint = goodReactionPoint + 1
WHERE id = #{relId}

UPDATE article
SET badReactionPoint = badReactionPoint + 1
WHERE id = #{relId}

UPDATE article
SET goodReactionPoint = goodReactionPoint - 1
WHERE id = #{relId}

UPDATE article
SET badReactionPoint = badReactionPoint - 1
WHERE id = #{relId}
