mapper.select:
SELECT A.*, M.nickname AS extra__writer FROM article AS A INNER JOIN `member` AS M ON A.memberId = M.id
AND A.BoardId = #{boardId}
ORDER BY A.id DESC LIMIT #{limitFrom}, #{itemsInAPage};

SELECT Count(*) AS cnt FROM article AS A INNER JOIN `member` AS M ON A.memberId = M.id
AND A.BoardId = #{boardId}
ORDER BY A.id DESC

mapper.update:
UPDATE article
title = #{title}, `body` = #{body}, updateDate= NOW(),
WHERE id = #{id}
